import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View,Image, SafeAreaView } from 'react-native';

export default function App() {
console.log(require("./assets/favicon.png")) // el require regresa un # de referencia de la imagen que estamos utilizando
  return (
    <View style={styles.container}>
      <Text 
      numberOfLines={3} //limita el # de lineas en el que el texto se truncara
      onPress={()=> alert("XD")}
      > 
        Esta es mi app con react native
        </Text>
        {/* //local image */}
        <Image source={{
          uri:"https://picsum.photos/200/300",
          width: 200,
          height: 300,
        }}/> 

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    alignContent: "center",
    alignItems:"center",
    justifyContent:"center"

  },
});
